import React from "react";
import "./App.css";

const questions = ["5!", "2!", "4!", "6!"]

const getRandomQuestion = () => {
  return setVal(Math.floor(Math.random() * questions.length));
}

function App() {
  const [text, setText] = React.useState("abcd");
  const [randVal, setVal] = React.useState(getRandomQuestion());

  const checkAnswer = () => {
    text == 120 ? alert("Correct") : alert("Incorrect")
  }

  const questionOutput = (arg) => {
    return <p>{arg}</p>
  }

  return (
    <div>
      <div>{questionOutput(questions[randVal])}</div>
      <input
        value={text}
        onChange={(event) => {
          setText(event.target.value);
        }}
      ></input>
      <button
        onClick={() => {
          getRandomQuestion();
          setText("");
        }}
      >
        Reset
      </button>
      <button type="submit" onClick={checkAnswer}>
        Sumbit
      </button>
    </div>
  );
}

export default App;
