import React from "react";
import "./App.css";



const getRandomQuestion = () => {
  return questions[(Math.floor(Math.random() * questions.length))];
}

function App() {
  const [text, setText] = React.useState("abcd");
  const [randVal, setVal] = React.useState(getRandomQuestion());

  const  questions = [{"5!" : 120}, {"2!": 2}, {"4!": 23}, {"6!": 222}]

  const checkAnswer = () => {
    text == 120 ? alert("Correct") : alert("Incorrect")
  }

  const questionOutput = (arg) => {
    return <p>{arg}</p>
  }

  return (
    <div>
      <div>{randVal}</div>
      <input
        value={text}
        onChange={(event) => {
          console.log(typeof event.target.value)
          setText(event.target.value);
        }}
      ></input>
      <button
        onClick={() => {
          getRandomQuestion();
          setText("");
        }}
      >
        Reset
      </button>
      <button type="submit" onClick={() => {checkAnswer}}>
        Sumbit
      </button>
    </div>
  );
}

export default App;
