import React from "react";
import "./App.css";

const questions = ["5!", "2!", "4!", "6!"]

const getRandomQuestion = () => {
  return questions[(Math.floor(Math.random() * questions.length))];
}

function App() {
  const [text, setText] = React.useState("abcd");
  const randVal = getRandomQuestion();

  const checkAnswer = () => {
    text == 120 ? alert("Correct") : alert("Incorrect")
  }


  return (
    <div>
      <div>{randVal}</div>
      <input
        value={text}
        onChange={(event) => {
          setText(event.target.value);
        }}
      ></input>
      <button
        onClick={() => {
          getRandomQuestion();
          setText("");
        }}
      >
        Reset
      </button>
      <button type="submit" onClick={() => {checkAnswer(); getRandomQuestion()}}>
        Sumbit
      </button>
    </div>
  );
}

export default App;
