import React from "react";
import "./App.css";

function App() {
  const [text, setText] = React.useState("abcd");
  const [randVal, setVal] = React.useState();

  const checkAnswer = () => {
    text == 120 ? alert("Correct") : alert("Incorrect")
  }

  const questionOutput = (arg) => {
    return <p>{arg}</p>
  }

  const questions = ["5!", "2!", "4!", "6!"]
  const newRand = Math.floor(Math.random() * questions.length);
  return (
    <div>
      <div>{questionOutput(questions[randVal])}</div>
      <input
        value={text}
        onChange={(event) => {
          setText(event.target.value);
        }}
      ></input>
      <button
        onClick={() => {
          setVal(newRand);
          setText("");
        }}
      >
        Reset
      </button>
      <button type="submit" onClick={
          [setVal(newRand),        
          checkAnswer]
        }>
        Sumbit
      </button>
    </div>
  );
}

export default App;
