import React from "react";
import "./App.css";

function App() {
  const [text, setText] = React.useState("abcd");

  const checkAnswer = () => {
    text == 120 ? alert("Correct") : alert("Incorrect")
  }

  const questionOutput = () => {
    const questions = ["5!", "2!", "4!", "6!"]
    return <p>{questions[Math.floor(Math.random() * questions.length)]}</p>
  }

  return (
    <div>
      <div>{questionOutput()}</div>
      <input
        value={text}
        // onChange={(event) => {
        //   setText(event.target.value);
        // }}
      ></input>
      <button
        onClick={() => {
          setText("");
        }}
      >
        Reset
      </button>
      <button type="submit" onClick={checkAnswer}>
        Sumbit
      </button>
    </div>
  );
}

export default App;
