import React from "react";
import "./App.css";

function App() {
  const [text, setText] = React.useState("abcd");

  const checkAnswer = () => {
    text == 120 ? alert("Correct") : alert("Incorrect")
  }

  return (
    <div>
      <div>Some question</div>
      <input
        value={text}
        onChange={(event) => {
          setText(event.target.value);
        }}
      ></input>
      <button
        onClick={() => {
          setText("");
        }}
      >
        Reset
      </button>
      <button type="submit" onSubmit={checkAnswer}>
        Sumbit
      </button>
    </div>
  );
}

export default App;
