import React from "react";
import "./App.css";

function App() {
  const [text, setText] = React.useState();

  function checkAnswer() {
    text === 120 ? alert("Correct!") : alert("Incorrect!")
  }

  return (
    <div>
      <div> 5! = </div>
      <input
        value={text}
        onChange={(event) => {
          setText(event.target.value);
        }}
      ></input>
      <button
        onClick={() => {
          setText("");
        }}
      >
        Reset
      </button>
      <button type='submit' onClick={checkAnswer}>
        Submit
      </button>
    </div>
  );
}

export default App;
