import React from "react";
import "./App.css";

function App() {
  const [text, setText] = React.useState("abcd");

  const checkAnswer = () => {
    text == 120 ? alert("Correct") : alert("Incorrect")
  }

  const questions = ["5!", "2!", "4!", "6!"]

  return (
    <div>
      <div>{this.questions.map((q) => {q})}</div>
      <input
        value={text}
        onChange={(event) => {
          setText(event.target.value);
        }}
      ></input>
      <button
        onClick={() => {
          setText("");
        }}
      >
        Reset
      </button>
      <button type="submit" onClick={checkAnswer}>
        Sumbit
      </button>
    </div>
  );
}

export default App;
